mapper.select:
SELECT DISTINCT A.no, A.name, A.type, A.content FROM (
SELECT board_no AS no, board_name AS name, board_type AS type, board_content AS content FROM MGV_MOVIE_BOARD WHERE board_no = #{no}
SELECT board_no AS no, board_name AS name, board_type AS type, board_content AS content FROM MGV_THEATER_BOARD WHERE board_no = #{no}
SELECT board_no AS no, board_name AS name, board_type AS type, board_content AS content FROM MGV_PRODUCT_BOARD WHERE board_no = #{no}
SELECT board_no AS no, board_name AS name, board_type AS type, board_content AS content FROM PARTY_BOARD WHERE board_no = #{no}
) as A

choose.when:
update MGV_MOVIE_BOARD


		board_deleted = #{deleted}


		board_report = #{report}


		report_count = #{reportCount}


where board_no = #{no}

update MGV_THEATER_BOARD


		board_deleted = #{deleted}


		board_report = #{report}


		report_count = #{reportCount}


where board_no = #{no}

update MGV_PRODUCT_BOARD


		board_deleted = #{deleted}


		board_report = #{report}


		report_count = #{reportCount}


where board_no = #{no}

update PARTY_BOARD


		PARTY_deleted = #{deleted}


		board_report = #{report}


		BOARD_report_count = #{reportCount}


where board_no = #{no}
