mapper.select:
select *
from ( select board_no as no, board_name as name, board_create_date as createDate, board_update_date as updateDate, board_read_cnt as readCount, board_comment_cnt as commentCount, user_id as "user.id", board_content as content, row_number() over(
order by board_create_date desc
order by board_create_date desc
order by board_create_date asc
order by board_read_cnt desc, board_create_date desc
order by board_comment_cnt desc, board_create_date desc
) as ROWNUMBER from MGV_PRODUCT_BOARD where board_deleted = 'N' and board_report = 'N'
and board_name like CONCAT('%', #{keyword}, '%')
and board_content like CONCAT('%', #{keyword}, '%')
and board_name like CONCAT('%', #{keyword}, '%') or board_content like CONCAT('%', #{keyword}, '%')
and user_id like CONCAT('%', #{keyword}, '%')
and product_no = #{productNo}
and cat_no = #{catNo}
) A having A.ROWNUMBER between #{begin} and #{end}
