mapper.select:
select count(*)
FROM MGV_SUPPORT_LOST A, MGV_LOCATION B , MGV_THEATERS C
where 
	A.LOCATION_NO = B.LOCATION_NO
	and A.theater_NO = C.theater_NO
	and A.LOST_DELETED = 'N'

		and A.user_id = #{userId}


		and A.LOCATION_NO = #{locationNo}


		and A.THEATER_NO = #{theaterNo}


		and A.LOST_ANSWERED = #{answered}


		and A.LOST_TITLE like  CONCAT('%', #{keyword}, '%')

SELECT A.Lost_NO as no, A.LOST_TITLE as title, A.LOST_CONTENT as content, A.LOST_ANSWERED as answered, A.LOST_DELETED as deleted, A.LOST_UPDATE_DATE as updateDate, A.LOST_CREATE_DATE as createDate, A.USER_ID as "user.id", A.LOCATION_NO as "location.no", B.Location_name as "location.name", A.THEATER_NO as "theater.no", C.theater_name as "theater.name" FROM MGV_SUPPORT_LOST A, MGV_LOCATION B , MGV_THEATERS C where A.LOCATION_NO = B.LOCATION_NO and A.theater_NO = C.theater_NO and A.LOST_DELETED = 'N'
and A.USER_ID = #{userId}
and A.LOCATION_NO = #{locationNo}
and A.THEATER_NO = #{theaterNo}
and A.LOST_ANSWERED = #{answered}
and A.LOST_TITLE like CONCAT('%', #{keyword}, '%')
order by A.Lost_NO DESC LIMIT 10 OFFSET #{begin}
