mapper.select:
select count(*) FROM MGV_SUPPORT_NOTICE A left outer join MGV_LOCATION B on A.LOCATION_NO = B.LOCATION_NO left outer join MGV_THEATERS C on A.THEATER_NO = C.THEATER_NO inner join MGV_SUPPORT_CATEGORIES D on A.CATEGORY_NO = D.CATEGORY_NO where A.NOTICE_DELETED = 'N'
and A.CATEGORY_NO = #{catNo}
and A.LOCATION_NO = #{locationNo}
and A.THEATER_NO = #{theaterNo}
and A.NOTICE_TITLE like CONCAT('%', #{keyword}, '%')

SELECT A.NOTICE_NO as no, A.NOTICE_TITLE as title, A.NOTICE_CONTENT as content, A.NOTICE_DELETED as deleted, A.NOTICE_UPDATE_DATE as updateDate, A.NOTICE_CREATE_DATE as createDate, A.LOCATION_NO as "location.no", B.LOCATION_NAME as "location.name", A.THEATER_NO as "theater.no", C.THEATER_NAME as "theater.name", A.CATEGORY_NO as "category.no", D.CATEGORY_NAME as "category.name", A.NOTICE_TYPE as type FROM MGV_SUPPORT_NOTICE A left outer join MGV_LOCATION B on A.LOCATION_NO = B.LOCATION_NO left outer join MGV_THEATERS C on A.THEATER_NO = C.THEATER_NO inner join MGV_SUPPORT_CATEGORIES D on A.CATEGORY_NO = D.CATEGORY_NO where A.NOTICE_DELETED = 'N'
and A.CATEGORY_NO = #{catNo}
and A.LOCATION_NO = #{locationNo}
and A.THEATER_NO = #{theaterNo}
and A.NOTICE_TITLE like CONCAT('%', #{keyword}, '%')
order by A.NOTICE_NO DESC LIMIT 10 OFFSET #{begin}
