mapper.select:
select count(*) FROM MGV_SUPPORT_ONE A where A.USER_ID = #{userId} and A.ONE_DELETED = 'N'
and A.ONE_ANSWERED = #{answered}
and A.ONE_TITLE like CONCAT('%', #{keyword}, '%')

SELECT A.ONE_NO as no, A.ONE_TITLE as title, A.ONE_CONTENT as content, A.ONE_ANSWERED as answered, A.ONE_DELETED as deleted, A.ONE_FILENAME as fileName, A.ONE_UPDATE_DATE as updateDate, A.ONE_CREATE_DATE as createDate, A.USER_ID as "user.id", A.LOCATION_NO as "location.no", B.LOCATION_NAME as "location.name", A.THEATER_NO as "theater.no", C.THEATER_NAME as "theater.name", A.CATEGORY_NO as "category.no", D.CATEGORY_NAME as "category.name" FROM MGV_SUPPORT_ONE A left outer join MGV_LOCATION B on A.LOCATION_NO = B.LOCATION_NO left outer join MGV_THEATERS C on A.THEATER_NO = C.THEATER_NO inner join MGV_SUPPORT_CATEGORIES D on A.CATEGORY_NO = D.CATEGORY_NO WHERE A.USER_ID = #{userId} and A.ONE_DELETED = 'N'
and A.ONE_ANSWERED = #{answered}
and A.ONE_TITLE like CONCAT('%', #{keyword}, '%')
order by A.ONE_NO DESC LIMIT 10 OFFSET #{begin}
