mapper.select:
select * from (SELECT ROW_NUMBER() OVER (ORDER BY A.PURCHASE_DATE DESC) as ROW_NUM, A.PURCHASE_NO as "no", A.PURCHASE_DATE as purchaseDate, A.PURCHASE_CANCELED_DATE as canceledDate, C.PRODUCT_NAME as "product.name", A.PURCHASE_PRICE as price, A.PURCHASE_STATUS as status, B.USER_ID as "user.id" FROM MGV_PURCHASE A JOIN MGV_USER B on A.USER_ID = B.USER_ID JOIN MGV_PRODUCT C ON A.PRODUCT_NO = C.PRODUCT_NO WHERE B.USER_ID = #{userId}
AND A.PURCHASE_STATUS = #{status}
AND A.PURCHASE_DATE BETWEEN #{startDate} AND #{endDate}) as PURCHASE_ROW WHERE ROW_NUM BETWEEN #{begin} and #{end};

SELECT COUNT(*)
FROM
    MGV_PURCHASE
WHERE
    USER_ID = #{userId}

        AND PURCHASE_STATUS = #{status}

        AND PURCHASE_DATE BETWEEN #{startDate} AND #{endDate}

mapper.update:
UPDATE MGV_PURCHASE
SET PURCHASE_STATUS = 'C'
WHERE PURCHASE_NO = #{purchaseNo}
